import { useEffect, useState } from "react"
import bookService from "../services/book.services";
import { Link } from "react-router-dom";
function BookCard(){
    const [books,setBooks] = useState([]);
    useEffect(()=>{
        getBooks();
    },[])
    async function getBooks(){
        try{
        const result=await bookService.getAllBooks()
        setBooks(result.data)   
        }catch(error){
                console.log(error)
        }
    }
    return(
        <div class="card-group container mt-auto">
            <div class="row">
            {books.map((elem,i)=>{
                return   <div class="col-sm" key={i+1}>
                <img class="card-img-top" src={`${elem.image}`} alt="Card image cap"/>
                <div class="card-body">
                    <h5 class="card-title">{elem.name}</h5>
                    <p class="card-text">{elem.auteur}</p>
                </div>
                <div class="card-footer">
                    <small class="text-muted">{elem.category.name}</small>
                </div>
                </div>
            })}        
        </div>
    </div>
    )
}

export default BookCard
