import { useEffect, useState } from "react"
import {useParams } from "react-router-dom";
import bookService from "../services/book.services";
import detail from "../detail.css"
function BookDetail(){
    const [name,setName]=useState("");
    const [description,setDescription]=useState("");
    const [isbn,setIsbn]=useState("");
    const [auteur,setAuteur]=useState("");
    const [editeur,setEditeur]=useState("");
    const [image,setImage]=useState("");
    const [category,setCategory]=useState({});
    const {id}=useParams()

    useEffect(()=>{
        getBookById();
    },[])

    async function getBookById(){
        const rep=await bookService.getBookById(id)
        setName(rep.data.name)
        setDescription(rep.data.description)
        setIsbn(rep.data.isbn)
        setAuteur(rep.data.auteur)
        setEditeur(rep.data.editeur)
        setImage(rep.data.image)
        setCategory(rep.data.category)
    }
    return(        
        <div>
        <div class="container">
        <div class="imgBx">
            <img src={`${image}`}/>
        </div>
        <div class="details">
            <div class="content">
                <h2>{name}<br/>
                    <span>Books Collection</span>
                </h2>
                <p>
                    {description}
                </p>
                <p>
                    Category : {category}
                </p>
                <p>
                    Isbn : {isbn}
                </p>                 
                <h3>{auteur}</h3>
                <h3>{editeur}</h3>
            </div>
        </div>
    </div>
    </div>
    )
}
export default BookDetail
