import { useEffect, useState } from "react"
import {useParams } from "react-router-dom";
import bookService from "../services/book.services";
function BookDetail(){
    const [name,setName]=useState("");
    const [description,setDescription]=useState("");
    const [isbn,setIsbn]=useState("");
    const [auteur,setAuteur]=useState("");
    const [editeur,setEditeur]=useState("");
    const [image,setImage]=useState("");
    const [category,setCategory]=useState({});
    const {id}=useParams()

    useEffect(()=>{
        getBookById();
        console.log(name)
    },[])

    async function getBookById(){
        const rep=await bookService.getBookById(id)
        setName(rep.data.name)
        setDescription(rep.data.description)
        setIsbn(rep.data.isbn)
        setAuteur(rep.data.auteur)
        setEditeur(rep.data.editeur)
        setImage(rep.data.image)
        setCategory(rep.data.category)
        console.log(rep)
    }
    return(  
        <div>
            <div class="card mb-3">
            <div class="row g-0">
                <div class="col-md-4">
                <img src={`${image}`} class="img-fluid rounded-start" alt="..."/>
                </div>
                <div class="col-md-8">
                <div class="card-body">
                    <h5 class="card-title">{name}</h5>
                    <p class="card-text">{description}</p>
                    <p class="card-text">Category : {category}</p>
                    <p class="card-text">Isbn : {isbn}</p>
                    <p class="card-text">Auteur : {auteur}</p>
                    <p class="card-text">Editeur : {editeur}</p>
                    <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                </div>
                </div>
            </div>
        </div>
    </div>
    )
}
export default BookDetail
