import { useState } from "react"
import { useNavigate } from "react-router-dom";
import bookService from "../services/book.services";

function BookNew(){
    const [name,setName]=useState("");
    const [description,setDescription]=useState("");
    const [isbn,setIsbn]=useState("");
    const [auteur,setAuteur]=useState("");
    const [editeur,setEditeur]=useState("");
    const [image,setImage]=useState("");
    const [category,setCategory]=useState("");
    const navigate=useNavigate();
    
    async function submitBook(e){
        e.preventDefault()
        const b = {"name":name,"description":description,"isbn":isbn,"auteur":auteur,"editeur":editeur,"image":image,"category":category}
        await bookService.addBook(b)
        navigate("/admin/books")
    }
    return(
        <form>
        <div class="form-group">
            <label for="nameBookInput">Name</label>
            <input type="text" class="form-control" id="nameBookInput" placeholder="Enter Book Name"/>
        </div>
        <div class="form-group">
            <label for="descriptionBookInput">Description</label>
            <input type="text" class="form-control" id="descriptionBookInput" placeholder="Enter Book Description"/>
        </div>        
        <button type="submit" class="btn btn-primary">Submit</button>
        </form>
    )
}
export default BookNew
