import { useEffect, useState } from "react"
import axios from 'axios';
import bookService from "../services/book.services";
function BookList(){
    const [books,setBooks] = useState([]);
    useEffect(()=>{
        getBooks();
    },[])
    async function getBooks(){
        try{
        const result=await axios.get("http://localhost:9090/books");
        setBooks(result.data)   
        }catch(error){
                console.log(error)
        }
    }
    useEffect(() =>{
        getBooks()
    },[])
    return(
        <table>
            <tr>
                <th>Name</th>
                <th>Description</th>
                <th>Isbn</th>
                <th>Auteur</th>
                <th>Editeur</th>
                <th>Date publication</th>
                <th>category</th>
            </tr>
            {books.map((elem,i)=>{
                return <tr key={i}>
                    <td>{elem.name}</td>
                    <td>{elem.description}</td>
                    <td>{elem.isbn}</td>
                    <td>{elem.auteur}</td>
                    <td>{elem.editeur}</td>
                    <td>{elem.date_publication}</td>
                    <td>{elem.category.name}</td>
                </tr>
            })}
        </table>
    )
}

export default BookList
